import React from 'react';

const Navigation = ({ currentSection, setCurrentSection }) => {
  const navItems = [
    {
      id: 'learn',
      label: 'Learn',
      icon: 'ðŸ“š',
      ariaLabel: 'Learn Korean letters and pronunciation'
    },
    {
      id: 'practice',
      label: 'Practice',
      icon: 'ðŸŽ¯',
      ariaLabel: 'Practice exercises for Korean letters'
    },
    {
      id: 'progress',
      label: 'Progress',
      icon: 'ðŸ“Š',
      ariaLabel: 'View your learning progress and achievements'
    },
    {
      id: 'cheatsheet',
      label: 'Cheat Sheet',
      icon: 'ðŸ“‹',
      ariaLabel: 'Quick reference for all Korean letters'
    }
  ];

  return (
    <nav className="main-navigation" role="navigation" aria-label="Main navigation">
      <ul className="nav-list">
        {navItems.map(item => (
          <li key={item.id} className="nav-item">
            <button
              className={`nav-button ${currentSection === item.id ? 'active' : ''}`}
              onClick={() => setCurrentSection(item.id)}
              aria-label={item.ariaLabel}
              aria-pressed={currentSection === item.id}
            >
              <span className="nav-icon">{item.icon}</span>
              <span className="nav-label">{item.label}</span>
            </button>
          </li>
        ))}
      </ul>
    </nav>
  );
};

export default Navigation;
