import React, { useState, useEffect, useCallback } from 'react';
import { useProgress } from '../context/ProgressContext';

const PracticeSection = () => {
  const {
    getAvailableLetters,
    updateScore,
    getBestScore,
    isLetterCompleted
  } = useProgress();

  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showResult, setShowResult] = useState(false);
  const [questionCount, setQuestionCount] = useState(0);
  const [sessionStats, setSessionStats] = useState({
    correct: 0,
    incorrect: 0,
    currentStreak: 0,
    bestStreak: 0
  });
  const [practiceMode, setPracticeMode] = useState('identification');

  // Get available letters for practice
  const availableLetters = getAvailableLetters();

  // Generate a new question
  const generateQuestion = useCallback(() => {
    if (availableLetters.length === 0) return;

    let question, correctAnswer, options, selectedLetter;

    switch (practiceMode) {
      case 'identification':
        // Show Korean letter, ask for name or sound
        selectedLetter = availableLetters[Math.floor(Math.random() * availableLetters.length)];
        question = `What is the name of this Korean letter?`;
        correctAnswer = selectedLetter.name;
        options = [correctAnswer];

        // Add 3 incorrect options
        const otherLetters = availableLetters.filter(letter => letter.id !== selectedLetter.id);
        while (options.length < 4 && otherLetters.length > 0) {
          const randomIndex = Math.floor(Math.random() * otherLetters.length);
          const incorrectOption = otherLetters[randomIndex].name;
          if (!options.includes(incorrectOption)) {
            options.push(incorrectOption);
          }
          otherLetters.splice(randomIndex, 1);
        }
        break;

      case 'pronunciation':
        // Show Korean letter, ask for romanization
        selectedLetter = availableLetters[Math.floor(Math.random() * availableLetters.length)];
        question = `How do you romanize this letter?`;
        correctAnswer = selectedLetter.romanization;
        options = [correctAnswer];

        // Add 3 incorrect options
        const otherLetters2 = availableLetters.filter(letter => letter.id !== selectedLetter.id);
        while (options.length < 4 && otherLetters2.length > 0) {
          const randomIndex = Math.floor(Math.random() * otherLetters2.length);
          const incorrectOption = otherLetters2[randomIndex].romanization;
          if (!options.includes(incorrectOption)) {
            options.push(incorrectOption);
          }
          otherLetters2.splice(randomIndex, 1);
        }
        break;

      default:
        return;
    }

    // Shuffle options
    options = options.sort(() => Math.random() - 0.5);

    setCurrentQuestion({
      question,
      correctAnswer,
      options,
      letter: selectedLetter
    });
    setSelectedAnswer(null);
    setShowResult(false);
  }, [availableLetters, practiceMode]);

  // Initialize first question
  useEffect(() => {
    generateQuestion();
  }, [generateQuestion]);

  // Handle answer selection
  const handleAnswerSelect = (answer) => {
    setSelectedAnswer(answer);
    setShowResult(true);

    const isCorrect = answer === currentQuestion.correctAnswer;
    const newScore = isCorrect ? 100 : 0;

    // Update progress
    updateScore(currentQuestion.letter.id, newScore);

    // Update session stats
    setSessionStats(prev => ({
      correct: prev.correct + (isCorrect ? 1 : 0),
      incorrect: prev.incorrect + (isCorrect ? 0 : 1),
      currentStreak: isCorrect ? prev.currentStreak + 1 : 0,
      bestStreak: isCorrect ? Math.max(prev.bestStreak, prev.currentStreak + 1) : prev.bestStreak
    }));

    // Update global score and question count
    setScore(prev => prev + newScore);
    setQuestionCount(prev => prev + 1);

    // Auto-advance to next question after 2 seconds
    setTimeout(() => {
      generateQuestion();
    }, 2000);
  };

  // Get practice mode description
  const getPracticeModeDescription = () => {
    switch (practiceMode) {
      case 'identification':
        return 'Identify Korean letters by their names';
      case 'pronunciation':
        return 'Practice Korean letter romanization';
      default:
        return 'Practice Korean letters';
    }
  };

  // Calculate session accuracy
  const getSessionAccuracy = () => {
    if (questionCount === 0) return 0;
    return Math.round((sessionStats.correct / questionCount) * 100);
  };

  if (availableLetters.length === 0) {
    return (
      <div className="practice-section">
        <div className="section-header">
          <h2>Practice Korean Letters</h2>
          <p>No letters available for practice. Complete some letters in the Learn section first!</p>
        </div>
      </div>
    );
  }

  return (
    <div className="practice-section">
      <div className="section-header">
        <h2>Practice Korean Letters</h2>
        <p>{getPracticeModeDescription()}</p>

        {/* Practice Mode Selection */}
        <div className="practice-mode-selection">
          <div className="mode-buttons">
            <button
              className={`mode-btn ${practiceMode === 'identification' ? 'active' : ''}`}
              onClick={() => setPracticeMode('identification')}
            >
              Letter Names
            </button>
            <button
              className={`mode-btn ${practiceMode === 'pronunciation' ? 'active' : ''}`}
              onClick={() => setPracticeMode('pronunciation')}
            >
              Romanization
            </button>
          </div>
        </div>

        {/* Session Stats */}
        <div className="session-stats">
          <div className="stat-item">
            <span className="stat-label">Questions:</span>
            <span className="stat-value">{questionCount}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Correct:</span>
            <span className="stat-value correct">{sessionStats.correct}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Accuracy:</span>
            <span className="stat-value">{getSessionAccuracy()}%</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Best Streak:</span>
            <span className="stat-value">{sessionStats.bestStreak}</span>
          </div>
        </div>
      </div>

      {currentQuestion && (
        <div className="practice-content">
          <div className="question-card">
            <div className="question-header">
              <h3>Question {questionCount + 1}</h3>
            </div>

            <div className="question-main">
              <div className="letter-display-large">
                <span className="korean-letter-large">{currentQuestion.letter.koreanLetter}</span>
                <div className="letter-hint">
                  <span className={`category-hint ${currentQuestion.letter.category}`}>
                    {currentQuestion.letter.category}
                  </span>
                  <span className={`difficulty-hint difficulty-${currentQuestion.letter.difficulty}`}>
                    Level {currentQuestion.letter.difficulty}
                  </span>
                </div>
              </div>

              <div className="question-text">
                <p>{currentQuestion.question}</p>
              </div>

              <div className="answer-options">
                {currentQuestion.options.map((option, index) => (
                  <button
                    key={index}
                    className={`answer-option ${
                      showResult
                        ? option === currentQuestion.correctAnswer
                          ? 'correct'
                          : selectedAnswer === option
                          ? 'incorrect'
                          : ''
                        : selectedAnswer === option
                        ? 'selected'
                        : ''
                    }`}
                    onClick={() => !showResult && handleAnswerSelect(option)}
                    disabled={showResult}
                  >
                    <span className="option-letter">{String.fromCharCode(65 + index)}</span>
                    <span className="option-text">{option}</span>
                    {showResult && option === currentQuestion.correctAnswer && (
                      <span className="result-icon correct">✓</span>
                    )}
                    {showResult && selectedAnswer === option && option !== currentQuestion.correctAnswer && (
                      <span className="result-icon incorrect">✗</span>
                    )}
                  </button>
                ))}
              </div>

              {showResult && (
                <div className="result-feedback">
                  <div className={`feedback ${selectedAnswer === currentQuestion.correctAnswer ? 'correct' : 'incorrect'}`}>
                    <p>
                      {selectedAnswer === currentQuestion.correctAnswer
                        ? 'Correct! Well done!'
                        : `Incorrect. The correct answer is "${currentQuestion.correctAnswer}".`
                      }
                    </p>
                  </div>

                  <div className="letter-info">
                    <h4>{currentQuestion.letter.name} ({currentQuestion.letter.koreanLetter})</h4>
                    <p><strong>Romanization:</strong> {currentQuestion.letter.romanization}</p>
                    <p><strong>Pronunciation:</strong> {currentQuestion.letter.englishComparison}</p>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Progress Summary */}
          <div className="progress-summary">
            <h4>Your Progress</h4>
            <div className="progress-grid">
              <div className="progress-item">
                <span className="progress-label">Best Score:</span>
                <span className="progress-value">{getBestScore(currentQuestion.letter.id)}%</span>
              </div>
              <div className="progress-item">
                <span className="progress-label">Completed:</span>
                <span className="progress-value">
                  {isLetterCompleted(currentQuestion.letter.id) ? 'Yes' : 'No'}
                </span>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PracticeSection;
