import React, { useState } from 'react';
import { koreanAlphabet } from '../koreanAlphabetData';
import { useProgress } from '../context/ProgressContext';

const CheatSheetSection = () => {
  const { isLetterUnlocked, getAvailableLetters } = useProgress();
  const [filter, setFilter] = useState('all'); // 'all', 'consonants', 'vowels', 'completed'
  const [sortBy, setSortBy] = useState('id'); // 'id', 'name', 'difficulty'

  const availableLetters = getAvailableLetters();

  const getFilteredLetters = () => {
    let letters = availableLetters;

    switch (filter) {
      case 'consonants':
        letters = letters.filter(letter => letter.category === 'consonant');
        break;
      case 'vowels':
        letters = letters.filter(letter => letter.category === 'vowel');
        break;
      case 'completed':
        // We'll need to add this functionality to the progress context
        letters = letters; // For now, show all available
        break;
      default:
        // Show all available letters
        break;
    }

    // Sort letters
    switch (sortBy) {
      case 'name':
        letters = [...letters].sort((a, b) => a.name.localeCompare(b.name));
        break;
      case 'difficulty':
        letters = [...letters].sort((a, b) => a.difficulty - b.difficulty);
        break;
      default:
        // Sort by id
        letters = [...letters].sort((a, b) => a.id - b.id);
        break;
    }

    return letters;
  };

  const filteredLetters = getFilteredLetters();

  const LetterCard = ({ letter }) => (
    <div className={`cheatsheet-card ${letter.category}`}>
      <div className="card-header">
        <span className="korean-letter-large">{letter.koreanLetter}</span>
        <div className="letter-meta">
          <span className="letter-name">{letter.name}</span>
          <span className={`category-tag ${letter.category}`}>
            {letter.category}
          </span>
        </div>
      </div>

      <div className="card-content">
        <div className="letter-details">
          <div className="detail-row">
            <span className="detail-label">Romanization:</span>
            <span className="detail-value">{letter.romanization}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Sound:</span>
            <span className="detail-value">{letter.englishComparison}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Level:</span>
            <span className={`detail-value difficulty-${letter.difficulty}`}>
              {letter.difficulty}
            </span>
          </div>
        </div>

        {letter.strokeOrder && (
          <div className="stroke-order-mini">
            <span className="stroke-label">Strokes:</span>
            <div className="stroke-numbers">
              {letter.strokeOrder.map((stroke, index) => (
                <span key={index} className="mini-stroke">{stroke}</span>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );

  return (
    <div className="cheatsheet-section">
      <div className="section-header">
        <h2>Korean Alphabet Cheat Sheet</h2>
        <p>Quick reference for all Korean letters (Hangul)</p>

        {/* Filters and Sorting */}
        <div className="cheatsheet-controls">
          <div className="filter-controls">
            <div className="filter-group">
              <label htmlFor="filter-select">Filter:</label>
              <select
                id="filter-select"
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
                className="filter-select"
              >
                <option value="all">All Letters</option>
                <option value="consonants">Consonants</option>
                <option value="vowels">Vowels</option>
              </select>
            </div>

            <div className="filter-group">
              <label htmlFor="sort-select">Sort by:</label>
              <select
                id="sort-select"
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="filter-select"
              >
                <option value="id">Order</option>
                <option value="name">Name</option>
                <option value="difficulty">Difficulty</option>
              </select>
            </div>
          </div>
        </div>

        {/* Summary Stats */}
        <div className="cheatsheet-stats">
          <div className="stat">
            <span className="stat-value">{availableLetters.length}</span>
            <span className="stat-label">Available</span>
          </div>
          <div className="stat">
            <span className="stat-value">
              {availableLetters.filter(l => l.category === 'consonant').length}
            </span>
            <span className="stat-label">Consonants</span>
          </div>
          <div className="stat">
            <span className="stat-value">
              {availableLetters.filter(l => l.category === 'vowel').length}
            </span>
            <span className="stat-label">Vowels</span>
          </div>
        </div>
      </div>

      <div className="cheatsheet-content">
        {filteredLetters.length > 0 ? (
          <div className="cheatsheet-grid">
            {filteredLetters.map(letter => (
              <LetterCard key={letter.id} letter={letter} />
            ))}
          </div>
        ) : (
          <div className="no-letters">
            <p>No letters match the current filter.</p>
          </div>
        )}

        {/* Additional Information */}
        <div className="cheatsheet-info">
          <div className="info-section">
            <h3>üìù Korean Alphabet Basics</h3>
            <ul>
              <li><strong>14 Consonants (ÏûêÏùå)</strong> - Basic building blocks</li>
              <li><strong>10 Vowels (Î™®Ïùå)</strong> - Combine with consonants to make syllables</li>
              <li><strong>Syllable Blocks</strong> - Letters combine: consonant + vowel (+ consonant)</li>
              <li><strong>Stroke Order</strong> - Write from top to bottom, left to right</li>
            </ul>
          </div>

          <div className="info-section">
            <h3>üéØ Learning Tips</h3>
