import React from 'react';
import { koreanAlphabet } from '../koreanAlphabetData';
import { useProgress } from '../context/ProgressContext';

const ProgressSection = () => {
  const {
    getProgressMetrics,
    getUnlockedAchievements,
    getLockedAchievements,
    getAvailableLetters,
    isLetterUnlocked,
    isLetterCompleted,
    getBestScore,
    getOverallProgress,
    resetAllProgress
  } = useProgress();

  const metrics = getProgressMetrics();
  const unlockedAchievements = getUnlockedAchievements();
  const lockedAchievements = getLockedAchievements();
  const availableLetters = getAvailableLetters();

  const handleResetProgress = () => {
    if (window.confirm('Are you sure you want to reset all progress? This cannot be undone.')) {
      resetAllProgress();
    }
  };

  return (
    <div className="progress-section">
      <div className="section-header">
        <h2>Your Learning Progress</h2>
        <p>Track your Korean alphabet mastery journey</p>
      </div>

      <div className="progress-content">
        {/* Overall Progress */}
        <div className="progress-overview">
          <div className="progress-circle">
            <svg viewBox="0 0 36 36" className="circular-progress">
              <path
                className="circle-bg"
                d="M18 2.0845
                  a 15.9155 15.9155 0 0 1 0 31.831
                  a 15.9155 15.9155 0 0 1 0 -31.831"
                fill="none"
                stroke="#eee"
                strokeWidth="2"
              />
              <path
                className="circle-progress"
                strokeDasharray={`${getOverallProgress()}, 100`}
                d="M18 2.0845
                  a 15.9155 15.9155 0 0 1 0 31.831
                  a 15.9155 15.9155 0 0 1 0 -31.831"
                fill="none"
                stroke="#4CAF50"
                strokeWidth="2"
                strokeLinecap="round"
              />
              <text x="18" y="20.35" className="progress-text">
                {getOverallProgress()}%
              </text>
            </svg>
          </div>

          <div className="progress-stats">
            <div className="stat-item">
              <span className="stat-value">{metrics.completedLetters}</span>
              <span className="stat-label">Letters Completed</span>
            </div>
            <div className="stat-item">
              <span className="stat-value">{metrics.unlockedLetters}</span>
              <span className="stat-label">Letters Unlocked</span>
            </div>
            <div className="stat-item">
              <span className="stat-value">{metrics.averageScore}%</span>
              <span className="stat-label">Average Score</span>
            </div>
          </div>
        </div>

        {/* Detailed Progress Grid */}
        <div className="letters-progress">
          <h3>Letter Progress</h3>
          <div className="letters-grid">
            {koreanAlphabet.map(letter => {
              const unlocked = isLetterUnlocked(letter.id);
              const completed = isLetterCompleted(letter.id);
              const bestScore = getBestScore(letter.id);

              return (
                <div
                  key={letter.id}
                  className={`letter-progress-item ${unlocked ? 'unlocked' : 'locked'} ${completed ? 'completed' : ''}`}
                >
                  <div className="letter-info">
                    <span className="korean-letter">{letter.koreanLetter}</span>
                    <span className="letter-name">{letter.name}</span>
                  </div>
                  {unlocked && (
                    <div className="progress-info">
                      <div className={`completion-status ${completed ? 'completed' : 'in-progress'}`}>
                        {completed ? 'âœ“' : 'â—‹'}
                      </div>
                      {completed && (
                        <div className="best-score">
                          {bestScore}%
                        </div>
                      )}
                    </div>
                  )}
                  {!unlocked && (
                    <div className="lock-indicator">ðŸ”’</div>
                  )}
                </div>
              );
            })}
          </div>
        </div>

        {/* Achievements */}
        <div className="achievements-section">
          <h3>Achievements</h3>

          {unlockedAchievements.length > 0 && (
            <div className="unlocked-achievements">
              <h4>Unlocked ({unlockedAchievements.length})</h4>
              <div className="achievements-grid">
                {unlockedAchievements.map(achievement => (
                  <div key={achievement.id} className="achievement unlocked">
                    <div className="achievement-icon">{achievement.icon}</div>
                    <div className="achievement-info">
                      <h5>{achievement.name}</h5>
                      <p>{achievement.description}</p>
                      <small className="achievement-reward">{achievement.reward}</small>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {lockedAchievements.length > 0 && (
            <div className="locked-achievements">
              <h4>Locked ({lockedAchievements.length})</h4>
              <div className="achievements-grid">
                {lockedAchievements.map(achievement => (
                  <div key={achievement.id} className="achievement locked">
                    <div className="achievement-icon locked">ðŸ”’</div>
                    <div className="achievement-info">
                      <h5>{achievement.name}</h5>
                      <p>{achievement.description}</p>
                      <small className="achievement-reward">??? </small>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Reset Progress Button */}
        <div className="reset-section">
          <button
            className="reset-button"
            onClick={handleResetProgress}
            aria-label="Reset all progress and start over"
          >
            Reset All Progress
          </button>
          <p className="reset-warning">
            This will clear all your progress, scores, and achievements. This action cannot be undone.
          </p>
        </div>
      </div>
    </div>
  );
};

export default ProgressSection;
